sys_variable_value.value:
(function execute(inputs, outputs) {
var request = new sn_ws.RESTMessageV2();
//*base_url="https://eufrankfurt.stonebranchdev.cloud/resources"
base_url=inputs['universal_controller_url']+"resources"
var user_token="Bearer "+inputs['uac_user_personal_access_token']
var user = 'selfservice';
var password = 'Welcome@123';
var task_name=inputs['task_name'];
var msg="##";
var api_task_refer="";
var task_id_flag=false;
var task_name_flage=false;
//Identify the Key paramater for API Request
if (inputs['task_id'] != '') {
  gs.debug('Task ID is not null');
  api_task_refer=inputs['task_id']
  msg="Task ID provided:"+api_task_refer;
  task_id_flag=true;
} else if (inputs['task_name'] != '') {
  gs.debug('Task Name is not null');
  api_task_refer=inputs['task_name'];
  task_name_flag=true;
  msg="Task Name Provided:"+api_task_refer;
} else {
  gs.debug('Either Task Name or Task Id to be specified');
  msg="No Task Name or Task Id provided";
}
//********************************************************************
//** Section to Handle Release Job**********************************
//*********************************************************************
if (inputs['function1'] == "release_a_task"){
gs.info("Begin Processing to Release UAC Task");
msg="Handling Release UAC Job";
//request.setBasicAuth(user,password);
outputs.function1="In Function to release UAC Jobs";
uac_release_end_point=base_url+"/taskinstance/release";
request.setHttpMethod('POST');
request.setEndpoint(uac_release_end_point);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
if (task_id_flag){
	  msg=msg+"\n task id based request prepared"
	  var rel_payload=JSON.stringify({
		  id:inputs['task_id']
	  });
} else {
	msg=msg+"\n task Name based request prepared"
	var rel_payload=JSON.stringify({
		  name:inputs['task_name'],
		  criteria:inputs['criteria']
	  });
}
request.setRequestBody(rel_payload);
msg=msg+"\n"+rel_payload.toString()
var job_rel_response = request.execute();
 if (job_rel_response.getStatusCode() == 200) {
    // Success
	msg=msg+"\nJob Release completed successfully in UAC RC:"+job_rel_response.getStatusCode();
    outputs.body = job_rel_response.getBody();
  } else {
    // Error
	msg=msg+"\nJob Hold Error RC:"+job_rel_response.getStatusCode();
    outputs.body = job_rel_response.getErrorMessage();
  }
}

//********************************************************************
//** Section to Handle Hold Job**********************************
//*********************************************************************
if (inputs['function1'] == "hold_a_task"){
gs.info("Begin Processing to Hold UAC Task");
msg="Handling Hold UAC Job";
//request.setBasicAuth(user,password);
outputs.function1="In Function to Hold UAC Jobs";
uac_hold_end_point=base_url+"/taskinstance/hold";
request.setHttpMethod('POST');
request.setEndpoint(uac_hold_end_point);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
if (task_id_flag){
	  msg=msg+"\n task id based request prepared"
	  var hold_payload=JSON.stringify({
		  id:inputs['task_id']
	  });
} else {
	msg=msg+"\n task Name based request prepared"
	var hold_payload=JSON.stringify({
		  name:inputs['task_name'],
		  criteria:inputs['criteria']
	  });
}
request.setRequestBody(hold_payload);
msg=msg+"\n"+hold_payload.toString()
var job_hold_response = request.execute();
 if (job_hold_response.getStatusCode() == 200) {
    // Success
	msg=msg+"\nJob Hold completed successfully in UAC RC:"+job_hold_response.getStatusCode();
    outputs.body = job_hold_response.getBody();
  } else {
    // Error
	msg=msg+"\nJob Hold Error RC:"+job_hold_response.getStatusCode();
    outputs.body = job_hold_response.getErrorMessage();
  }
}
//********************************************************************
//** Section to Handle Force Finish of Job**********************************
//*********************************************************************
if (inputs['function1'] == "force_finish_a_task"){
gs.info("Begin Processing to Force Finish UAC Task");
msg="Handling Force Finish UAC Job";
//request.setBasicAuth(user,password);
outputs.function1="In Function to Force Finish UAC Jobs";
uac_ff_end_point=base_url+"/taskinstance/forcefinish";
request.setHttpMethod('POST');
request.setEndpoint(uac_ff_end_point);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
if (task_id_flag){
	  msg=msg+"\n task id based request prepared"
	  var ff_payload=JSON.stringify({
		  id:inputs['task_id']
	  });
} else {
	msg=msg+"\n task Name based request prepared"
	var ff_payload=JSON.stringify({
		  name:inputs['task_name'],
		  criteria:inputs['criteria']
	  });
}
request.setRequestBody(ff_payload);
msg=msg+"\n"+ff_payload.toString()
var job_ff_response = request.execute();
 if (job_ff_response.getStatusCode() == 200) {
    // Success
	msg=msg+"\nJob Forcefinish successfully in UAC RC:"+job_ff_response.getStatusCode();
    outputs.body = job_ff_response.getBody();
  } else {
    // Error
	msg=msg+"\nJob Force Finish Error RC:"+job_ff_response.getStatusCode();
    outputs.body = job_ff_response.getErrorMessage();
  }
}
//********************************************************************
//** Section to Handle Rerun of Job**********************************
//*********************************************************************
if (inputs['function1'] == "rerun_a_task"){
gs.info("Begin Processing to Rerun UAC Task");
msg="Handling Rerun UAC Job";
//request.setBasicAuth(user,password);
outputs.function1="In Function to Rerun UAC Jobs";
uac_launch_end_point=base_url+"/taskinstance/rerun";
request.setHttpMethod('POST');
request.setEndpoint(uac_launch_end_point);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
if (task_id_flag){
	  msg=msg+"\n task id based request prepared"
	  var rerun_payload=JSON.stringify({
		  id:inputs['task_id']
	  });
} else {
	msg=msg+"\n task Name based request prepared"
	var rerun_payload=JSON.stringify({
		  name:inputs['task_name'],
		  criteria:inputs['criteria']
	  });
}
request.setRequestBody(rerun_payload);
msg=msg+"\n"+rerun_payload.toString()
var job_rerun_response = request.execute();
 if (job_rerun_response.getStatusCode() == 200) {
    // Success
	msg=msg+"\nJob Rerun successfully in UAC RC:"+job_rerun_response.getStatusCode();
    outputs.body = job_rerun_response.getBody();
  } else {
    // Error
	msg=msg+"\nJob Rerun Error RC:"+job_rerun_response.getStatusCode();
    outputs.body = job_rerun_response.getErrorMessage();
  }
}
//**********************************************************************************
//*
//Section to launch UAC Job with Variables
if (inputs['function1'] == "launch_a_task_workflow_now"){
gs.info("Begin Processing to launch UAC Task");
msg="Handling Launch UAC Job";
//request.setBasicAuth(user,password);
outputs.function1="In Function to Launch UAC Jobs";
uac_launch_end_point=base_url+"/task/ops-task-launch";
request.setHttpMethod('POST');
request.setEndpoint(uac_launch_end_point);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
if (inputs['launch_task_with_variables']){
	//append variables from form in to json
	//  var variable_arr = [
	//  {inputs['specify_the_run_time_task_variables']
	//  }];
	  msg=msg+"\n Job Variables payload been set"
	  var input_json = JSON.parse(inputs['task_variables']);
	  var job_launch_payload=JSON.stringify({
		  name:inputs['task_name'],
		    "variables": input_json
	  });
} else {
	msg=msg+"\n No Job Variables"
	var job_launch_payload=JSON.stringify({
		  name:inputs['task_name']
	  });
}
//var jsonPayload = JSON.stringify(job_launch_payload);
request.setRequestBody(job_launch_payload);
msg=msg+"\n"+job_launch_payload.toString()
var job_launch_response = request.execute();
 if (job_launch_response.getStatusCode() == 200) {
    // Success
	msg=msg+"\nJob Launched successfully in UAC RC:"+job_launch_response.getStatusCode();
    outputs.body = job_launch_response.getBody();
  } else {
    // Error
	msg=msg+"\nJob Launched Error RC:"+job_launch_response.getStatusCode();
    outputs.body = job_launch_response.getErrorMessage();
  }
}

//section to handle Adhoc job
if (inputs['function1'] == "schedule_a_adhoc_job"){
//const date_time=inputs['tmp_schedule'].split(" ")
//var tmp_run_date=date_time[0]
uac_launch_end_point=base_url+"/trigger"
request.setEndpoint(uac_launch_end_point);
request.setHttpMethod('POST');
var tmp_run_date=inputs['tmp_schedule'].toString().slice(0, 10)
gs.info("Begin Processing")
//var ttime= date_time[1].slice(0, 5)
var ttime=inputs['tmp_schedule'].toString().slice(11,16)
var req_body = {
            type:'triggerTemp',
            tasks:[],
			description:"Trigger Created from ServiceNow",
            tdate:tmp_run_date,
            ttime:ttime,
           	name:inputs['trigger_name']
        };
req_body.tasks.push(task_name)
var tosend = JSON.stringify(req_body);
gs.info(tosend);
//request.setBasicAuth(user,password);
request.setRequestHeader("Accept","application/json");
request.setRequestHeader('Content-Type','application/json');
request.setRequestHeader('Authorization',user_token);
request.setRequestBody(tosend);
var response = request.execute();
outputs.body = response.getBody();
outputs.api_rc = response.getStatusCode();
msg=msg+"\nCreate Tmp trigger executed with RC:"+response.getStatusCode();

//Enable Trigger
if (outputs.api_rc == 200) {
  outputs.function1="In Function to enable trigger";
  msg=msg+"\n Request to Enable Tmp Trigger";
  var request1 = new sn_ws.RESTMessageV2();
  request1.setHttpMethod('POST');
  //request1.setBasicAuth(user,password);
  request1.setRequestHeader("Accept","application/json");
  request1.setRequestHeader('Content-Type','application/json');
  request1.setRequestHeader('Authorization',user_token);
  uac_trig_enable_end_point=base_url+"/trigger/ops-enable-disable-trigger"
  request1.setEndpoint(uac_trig_enable_end_point);
  var trig_req_body = [];
  var enable_req = [{
  	  name:inputs['trigger_name'],
  	  enable:'true'
  }];
  outputs.function1=enable_req
  var trig_enable_req=JSON.stringify(enable_req);
  outputs.print_values=trig_enable_req;
  request1.setRequestBody(trig_enable_req);

  var response1 = request1.execute();
  outputs.api_rc = response1.getStatusCode();
  msg=msg+"\n Trigger Enabled Return code:"+response1.getStatusCode();
  outputs.body = response1.getBody();
}
}
outputs.print_values=msg;
})(inputs, outputs);

