sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...

    var msg = "Handling UAC SAP Creation Event";
    var request = new sn_ws.RESTMessageV2();
    var uac_event="resources/universalevent/push/"+inputs['uac_event']+"?access_token="
    var base_url = inputs['uac_url']+uac_event+inputs['uac_pat'];

    // Define the JSON data you want to send in the request body
    var sap_data = {
    sap_task_name: inputs['sap_task_name'],
    sap_system: inputs['sap_system'],
    sap_task_description:inputs['sap_task_description'],
    requestor_email:inputs['requestor_email'],
    abap_name:inputs['abap_name'],
    variant_name:inputs['variant_name'],
    sap_batch_user:inputs['sap_batch_user'],
    printer:inputs['printer'],
    uac_trigger_name:inputs['uac_trigger_name'],
    time_style:inputs['time_style'],
    time_hh_mm:inputs['time_hh_mm'],
    schedule_type:inputs['schedule_type'],
    time_interval:inputs['time_interval'],
    time_interval_units:inputs['time_interval_units'],
    specific_days:inputs['specific_days'],
    snow_request_number:inputs['snow_request_number'],
    schedule_job:inputs['schedule_job']
    };

    var sap_job_definition = JSON.stringify(sap_data);
    outputs.print_values = base_url+"\n"+sap_job_definition;

    request.setHttpMethod('POST');
    request.setEndpoint(base_url);
    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestBody(sap_job_definition);

    var sap_job_push_action = request.execute();
       outputs.api_rc = sap_job_push_action.getStatusCode();

    if (sap_job_push_action.getStatusCode() === 200) {
        // Success
        msg = msg + "\nJob Creation Event Pushed to UAC: " + sap_job_push_action.getStatusCode();
        outputs.body = sap_job_push_action.getBody();
    } else {
        // Error
        msg = msg + "\nJob Creation Event Push Failed Error RC: " + sap_job_push_action.getStatusCode();

        // Check for a more detailed error message in the response body if available
        var errorResponse = sap_job_push_action.getBody();
        if (errorResponse) {
            try {
                var errorData = JSON.parse(errorResponse);
                outputs.error_message = errorData.message; // Customize this based on your response structure
            } catch (e) {
                outputs.error_message = "Unable to parse error response.";
            }
        } else {
            outputs.error_message = "No error message provided.";
        }
    }
    gs.info(msg);
})(inputs, outputs);

