sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...

    var msg = "Handling UAC TASK Creation Event";
    var request = new sn_ws.RESTMessageV2();
    var uac_event="resources/universalevent/push/"+inputs['uac_task_type']+"?access_token="
    var base_url = inputs['uac_url']+uac_event+inputs['uac_pat'];

    // Define the JSON data you want to send in the request body
    var task_data = {
      task_name: inputs['task_name'],
    task_description:"Task Created by ServiceNow RITM:"+inputs['snow_ritm_id'],
    requestor_email:inputs['requestor_email'],
    application_1:inputs['application_1'],
    cmd_to_execute:inputs['cmd_to_execute'],
    parameter_1:inputs['parameter_1'],
    email_alert_failure:inputs['email_alert_failure'],
    uac_trigger_name:inputs['uac_trigger_name'],
    time_style:inputs['time_style'],
    time_hh_mm:inputs['schedule_time_hh_mm'],
    schedule_type:inputs['schedule_type'],
    time_interval:inputs['time_interval'],
    time_interval_units:inputs['time_interval_units'],
    specific_days:inputs['specific_days'],
    snow_request_number:inputs['snow_ritm_id'],
    schedule_job:inputs['schedule_job']
    };

    var job_definition = JSON.stringify(task_data);
    outputs.print_values = base_url+"\n"+job_definition;

    request.setHttpMethod('POST');
    request.setEndpoint(base_url);
    request.setRequestHeader('Content-Type', 'application/json');
    request.setRequestBody(job_definition);

    var job_push_action = request.execute();
       outputs.api_rc = job_push_action.getStatusCode();

    if (job_push_action.getStatusCode() === 200) {
        // Success
        msg = msg + "\nJob Creation Event Pushed to UAC: " + job_push_action.getStatusCode();
        outputs.body = job_push_action.getBody();
    } else {
        // Error
        msg = msg + "\nJob Creation Event Push Failed Error RC: " + job_push_action.getStatusCode();

        // Check for a more detailed error message in the response body if available
        var errorResponse = job_push_action.getBody();
        if (errorResponse) {
            try {
                var errorData = JSON.parse(errorResponse);
                outputs.error_message = errorData.message;
            } catch (e) {
                outputs.error_message = "Unable to parse error response.";
            }
        } else {
            outputs.error_message = "No error message provided.";
        }
    }
    gs.info(msg);
})(inputs, outputs);

