sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    try {
        var r = new sn_ws.RESTMessageV2();
        r.setEndpoint("https://ec2-52-200-159-228.compute-1.amazonaws.com:8599/uc/resources/universalevent/push/snow_webhook?access_token=ucp_ZGwXzeCmQ8in0Jd1tkRbMGK473NIkiLpTHcmfp7s");
        r.setHttpMethod("post");


        // More info at: 
        // https://swissbytes.blogspot.com/2017/11/service-now-script-includes-make-sure.html

        var number = current.getValue("number");
        var short_description = current.getValue("short_description");
        var description = current.getValue("description");
        var state = current.getValue("state");
        var requested_by = current.getValue("requested_by");
        var type = current.getValue("type");
        var state = current.getValue("state");
        var service = current.getValue("service");

        var obj = {
            "number": number,
            "short_description": short_description,
            "description": description,
            "state": state,
            "requested_by": requested_by,
            "type": type,
            "service": service
        };

        var body = JSON.stringify(obj);
        gs.info("Webhook body: " + body);
        r.setRequestBody(body);

        var response = r.execute();
        var httpStatus = response.getStatusCode();
    } catch (ex) {
        var message = ex.message;
		gs.error("Error message: " + message);
    }

    gs.info("Webhook target HTTP status response: " + httpStatus);

})(current, previous);
